mapper.select:
SELECT DISTINCT p.playlist_id AS playlistId, p.playlist_name AS playlistName, p.playlist_cover AS playlistCover, u.user_name AS creatorName, p.create_time AS createTime, p.play_count AS playCount, p.status FROM playlist_info p LEFT JOIN sys_user u ON p.creator_id = u.user_id and u.delete_flag = 0 LEFT JOIN playlist_tags pt ON p.playlist_id = pt.playlist_id where p.delete_flag = 0
AND p.playlist_name LIKE CONCAT('%', #{playlistPageQry.playlistName}, '%')
AND pt.tag_id = #{playlistPageQry.tagId}
AND p.status = #{playlistPageQry.status}
ORDER BY p.create_time DESC
