SELECT s.song_id AS songId, s.song_name AS songName, s.song_duration AS songDuration, s.song_cover AS songCover, s.song_release_date AS songReleaseDate, s.song_lyrics AS songLyrics, s.song_file_path AS songFilePath, s.create_time AS createTime, s.update_time AS updateTime, si.singer_id AS singerId, si.singer_name AS singerName, ai.album_id AS albumId, ai.album_name AS albumName, sm.mv_id AS mvId FROM song_info s LEFT JOIN song_mv sm ON s.song_id = sm.song_id AND sm.delete_flag = 0 LEFT JOIN singer_info si ON s.singer_id = si.singer_id AND si.delete_flag = 0 LEFT JOIN album_info ai ON s.album_id = ai.album_id AND ai.delete_flag = 0 WHERE s.delete_flag = 0
AND s.song_name LIKE CONCAT('%', #{songQry.songName}, '%')
AND si.singer_id = #{songQry.singerId}
AND ai.album_id = #{songQry.albumId}

